import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native-web';
import ShowAvatar from '../Avatar/ShowAvatar/showAvatar';
import ForwardSvg from '../../assets/icons/forwardSvg';
import ForwardFocusSvg from '../../assets/icons/forwardFocusSvg';

const MessageBody = ({ imageUrl, nickname, role, message, own, channel }) => {
  const [isFocused, setIsFocused] = useState(false);
  const date = new Date();
  const time = date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  const [selectedMessages, setSelectedMessages] = useState([]);

  const handleForwardPress = () => {
    if (isFocused) {
      // Remove the message from the selectedMessages array
      setSelectedMessages(prevMessages => prevMessages.filter(message => message !== message));
    } else {
      // Add the message to the selectedMessages array
      setSelectedMessages(prevMessages => [...prevMessages, message]);
    }
    setIsFocused(prevState => !prevState);
  };
  

  const forwardIcon = isFocused ? <ForwardFocusSvg /> : <ForwardSvg />;

  return (
    <View style={own ? styles.ownContainer : styles.container}>
      {!own && (
        <View style={styles.avatar}>
          <ShowAvatar imageUrl={imageUrl} profile={false} />
        </View>
      )}
      <View
        style={[
          styles.messageBox,
          own ? styles.ownMessageBox : styles.box,
          {
            borderTopRightRadius: own ? 10 : 10,
            borderBottomRightRadius: own ? 0 : 10,
            borderBottomLeftRadius: own ? 10 : 0,
          },
        ]}
      >
        <TouchableOpacity onPress={handleForwardPress}>
          <View style={own ? styles.ownForward : styles.forward}>{forwardIcon}</View>
        </TouchableOpacity>
        <Text style={styles.nickname}>{nickname}</Text>
        {channel && <Text style={styles.role}>{role}</Text>}
        <Text style={styles.message}>{message}</Text>
        <Text style={own ? styles.ownTime : styles.time}>{time}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    marginBottom: 10,
    maxWidth: '100%',
  },
  ownContainer: {
    flexDirection: 'row-reverse',
    alignItems: 'flex-start',
    marginBottom: 10,
    maxWidth: '100%',
  },
  avatar: {
    alignSelf: 'flex-start',
    marginRight: 15,
  },
  messageBox: {
    backgroundColor: '#E7DEDE',
    paddingVertical: 10,
    paddingHorizontal: 15,
    maxWidth: '60%',
    marginBottom: 13
  },
  box: {
    borderTopRightRadius: 10,
    borderBottomRightRadius: 10,
    borderTopLeftRadius: 10,
  },
  ownMessageBox: {
    backgroundColor: 'rgba(0, 118, 185, 0.35)',
    paddingVertical: 10,
    paddingHorizontal: 15,
    maxWidth: '60%',
    borderTopRightRadius: 10,
    borderBottomRightRadius: 10,
    borderTopLeftRadius: 10,
    marginBottom: 13
  },
  forward: {
    alignSelf: 'flex-end',
    marginBottom: 5,
  },
  ownForward: {
    alignSelf: 'flex-start',
    marginBottom: 5,
  },
  nickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  role: {
    fontSize: 24,
    color: '#0076B9',
    marginBottom: 5,
  },
  message: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
    marginBottom: 5,
  },
  time: {
    fontSize: 14,
    color: 'black',
    alignSelf: 'flex-end',
  },
  ownTime: {
    fontSize: 14,
    color: 'black',
    alignSelf: 'flex-start',
  },
});

export default MessageBody;
