import React from "react";
import { View, StyleSheet, Text, TouchableOpacity, Image } from "react-native-web";
import ShowAvatar from "../Avatar/ShowAvatar/showAvatar";

export default function SearchBody({ avatarUrl, username, channelName, onPress, containerStyle }) {
  const [isHovered, setIsHovered] = React.useState(false);

  return (
    <TouchableOpacity
      style={[styles.container, containerStyle, isHovered && { backgroundColor: "#E7DEDE" }]}
      onPress={onPress}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {avatarUrl ? (
        <View style={styles.avatarContainer}>
          <ShowAvatar source={{ uri: avatarUrl }} style={styles.avatar} />
          <Text style={styles.username}>{username}</Text>
        </View>
      ) : (
        <Text style={styles.channelName}>{channelName}</Text>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#FFFFFF",
    borderRadius: 10,
    paddingVertical: 12,
    paddingHorizontal: 16,
    marginVertical: 8,
  },
  avatarContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  avatar: {
    width: 32,
    height: 32,
    borderRadius: 16,
    marginRight: 8,
  },
  username: {
    fontSize: 16,
    fontWeight: "bold",
  },
  channelName: {
    fontSize: 16,
  },
});
