import React, { useState, useEffect, useContext } from 'react';
import { View, Text, Button, TouchableHighlight, Alert } from 'react-native-web';
import useStyles from './styles/greetingsScreen.module';
import DataInput from '../components/inputs/textInput/textInput';
import HeaderButton from '../components/buttons/headerButton';
import BackSvg from '../assets/icons/backSvg';
import AuthContext from '../context/AuthContext';

function ChangePasswordScreen({ navigation }) {
  const styles = useStyles();
  const [inputText, setInputText] = useState({
    newPassword: '',
    password: '',
    confirmPassword: '',
  });
  const { user, updateUser } = useContext(AuthContext);
  const username = 'admin';
  const password = 'root';

  const isButtonDisabled = () => {
    return (
      !inputText.newPassword ||
      !inputText.password ||
      !inputText.confirmPassword 
    );
  };

  const changePassword = () => {
    if (inputText.password !== user?.password) {
      Alert.alert('Error', 'Passwords do not match');
      return;
    }
    if (inputText.newPassword !== inputText.confirmPassword) {
      Alert.alert('Error', 'New password do not match');
      return;
    }
  
    const userId = user?.id;
    const newPassword = encodeURIComponent(inputText.newPassword);
    const apiUrl = `http://localhost:8080/api/users/${userId}/update/password?newPassword=${newPassword}`;
  
    fetch(apiUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Basic ${btoa(`${username}:${password}`)}`,
      },
    })
      .then((response) => {
        if (response.ok) {
          Alert.alert('Success', 'Password updated successfully');
        } else {
          Alert.alert('Error', 'Failed to update password');
        }
      })
      .catch((error) => {
        console.error('Error updating password:', error);
        Alert.alert('Error', 'Failed to update password');
      });
  };

  return (
    <View style={styles.containerMain}>
      <View style={styles.textContainer}>
        <View style={styles.inputContainer}>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.password}
              setValue={(text) => setInputText({ ...inputText, password: text })}
              placeholder={'Пароль'}
              type={'password'}
              flex={false}
            />
          </View>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.newPassword}
              setValue={(text) => setInputText({ ...inputText, newPassword: text })}
              placeholder={'Новый пароль'}
              type={'password'}
              flex={false}
            />
          </View>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.confirmPassword}
              setValue={(text) => setInputText({ ...inputText, confirmPassword: text })}
              placeholder={'Подтвердить пароль'}
              type={'password'}
              flex={false}
            />
          </View>
        </View>

        <View>
          <HeaderButton title={'Изменить пароль'} onPress={changePassword} disabled={isButtonDisabled()} />
        </View>
      </View>
      <View style={styles.topLeft}>
        <TouchableHighlight onPress={() => navigation.navigate('Profile')}>
          <BackSvg />
        </TouchableHighlight>
      </View>
    </View>
  );
}

export default ChangePasswordScreen;
