import React, { useState } from 'react';
import { View, Text, TouchableHighlight, Modal, TouchableOpacity } from 'react-native-web';
import CreateSvg from '../assets/icons/createSvg';
import useStyles from './styles/mainAuthScreen.module';
import SearchInput from '../components/inputs/searchInput/searchInput';
import HeaderButton from '../components/buttons/headerButton';
import DataInput from '../components/inputs/textInput/textInput';

export default function MainAuthScreen({ navigation }) {
  const styles = useStyles();
  const [showPopup, setShowPopup] = useState(false);
  const [inputText, setInputText] = useState({
    nickname: '',
    email: '',
    password: ''
  });

  return (
    <View style={styles.containerMain}>
      <View style={styles.messageContainer}>
        <Text style={styles.text}>Зарегистрируйтесь, чтобы начать общаться</Text>
      </View>
      <View style={styles.bottomLeft}>
        <TouchableHighlight onPress={() => setShowPopup(true)}>
          <CreateSvg />
        </TouchableHighlight>
      </View>
      <Modal visible={showPopup} transparent={true}>
        <View style={styles.popupContainer}>
        <Text style={styles.text}>Название канала</Text>
        <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.password}
              setValue={(text) => setInputText({ ...inputText, password: text })}
              placeholder={""}
              type={"nickname"}
            />
          </View>
          <TouchableOpacity onPress={() => setShowPopup(false)}>
          <HeaderButton title={"Зарегистрироваться"} onPress={() => navigation.navigate('Auth')} disabled={!isFormValid} />
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}
