import {  StyleSheet } from 'react-native';

export default function useStyles() {

    return StyleSheet.create({
        container: {
            flex: 1,
            backgroundColor: '#FFFFFF',
            justifyContent: 'center',
            alignItems: 'center',
          },
          headerButton: {
            marginTop: 80, // задаем отступ сверху для кнопки
          },
        
        });
}
