import React, { useState, useContext } from 'react';
import { View, TouchableHighlight } from 'react-native';
import useStyles from './styles/greetingsScreen.module';
import DataInput from '../components/inputs/textInput/textInput';
import HeaderButton from '../components/buttons/headerButton';
import BackSvg from '../assets/icons/backSvg';
import AuthContext from '../context/AuthContext';

function ChangePasswordScreen({ navigation }) {
  const styles = useStyles();
  const [inputText, setInputText] = useState({
    newPassword: '',
    password: '',
    confirmPassword: '',
  });

  const { user } = useContext(AuthContext);
  const id = user?.id; // Assuming the user object has an 'id' property

  const isButtonDisabled = () => {
    return (
      !inputText.newPassword ||
      !inputText.password ||
      !inputText.confirmPassword ||
      inputText.newPassword !== inputText.confirmPassword
    );
  };

  const handleUpdatePassword = async () => {
    if (inputText.newPassword !== inputText.confirmPassword){

    }E
    try {
      const queryParams = `last_password=${encodeURIComponent(inputText.password)}&new_password=${encodeURIComponent(inputText.newPassword)}`;
      const response = await fetch(`http://localhost:8080/api/users/${id}/update/password?${queryParams}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Basic ${btoa(`${username}:${password}`)}`,
        },
      });
  
      if (response.ok) {
        // Password update successful
        alert('Paswordd updated');
      } else {
        // Handle error response
        console.error('Failed to update password');
      }
    } catch (error) {
      console.error('Error updating password:', error);
    }
  };

  return (
    <View style={styles.containerMain}>
      <View style={styles.textContainer}>
        <View style={styles.inputContainer}>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.password}
              setValue={(text) => setInputText({ ...inputText, password: text })}
              placeholder="Пароль"
              type="password"
              flex={false}
            />
          </View>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.newPassword}
              setValue={(text) => setInputText({ ...inputText, newPassword: text })}
              placeholder="Новый пароль"
              type="password"
              flex={false}
            />
          </View>
          <View style={{ marginBottom: 13 }}>
            <DataInput
              value={inputText.confirmPassword}
              setValue={(text) => setInputText({ ...inputText, confirmPassword: text })}
              placeholder="Подтвердить пароль"
              type="password"
              flex={false}
            />
          </View>
        </View>

        <View>
          <HeaderButton
            title="Изменить пароль"
            onPress={handleUpdatePassword}
            disabled={isButtonDisabled()}
          />
        </View>
      </View>
      <View style={styles.topLeft}>
        <TouchableHighlight onPress={() => navigation.navigate('Profile')}>
          <BackSvg />
        </TouchableHighlight>
      </View>
    </View>
  );
}

export default ChangePasswordScreen;
