import React, { createContext, useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [currentScreen, setCurrentScreen] = useState(null);

  useEffect(() => {
    checkLoginStatus();
    checkCurrentScreen();
  }, []);

  const checkLoginStatus = async () => {
    try {
      const isLoggedIn = await AsyncStorage.getItem('isLoggedIn');
      const userData = await AsyncStorage.getItem('user');
      if (isLoggedIn === 'true' && userData) {
        setIsLoggedIn(true);
        setUser(JSON.parse(userData));
      }
    } catch (error) {
      console.error('Error during checkLoginStatus:', error);
    }
  };

  const checkCurrentScreen = () => {
    const storedScreen = localStorage.getItem('currentScreen');
    setCurrentScreen(storedScreen);
  };

  const saveCurrentScreen = (screen) => {
    setCurrentScreen(screen);
    localStorage.setItem('currentScreen', screen);
  };

  const login = async (userData) => {
    // Perform login logic
    setIsLoggedIn(true);
    setUser(userData);
    try {
      await AsyncStorage.setItem('isLoggedIn', 'true');
      await AsyncStorage.setItem('user', JSON.stringify(userData));
      localStorage.getItem('currentScreen', screen);
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const logout = async () => {
    setIsLoggedIn(false);
    setUser(null);
    try {
      await AsyncStorage.removeItem('isLoggedIn');
      await AsyncStorage.removeItem('user');
      localStorage.removeItem('currentScreen');
      // Add the redirection logic here
      navigation.navigate('Auth'); // Assuming you have a navigation object available
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  const authContextValue = {
    isLoggedIn,
    user,
    login,
    logout,
    currentScreen,
    saveCurrentScreen,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
