import React, { createContext, useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [activeScreen, setActiveScreen] = useState(null);

  useEffect(() => {
    checkLoginStatus();
    loadActiveScreen();
  }, []);

  const checkLoginStatus = async () => {
    try {
      const isLoggedIn = await AsyncStorage.getItem('isLoggedIn');
      const userData = await AsyncStorage.getItem('user');
      if (isLoggedIn === 'true' && userData) {
        setIsLoggedIn(true);
        setUser(JSON.parse(userData));
      }
    } catch (error) {
      console.error('Error checking login status:', error);
    }
  };

  const loadActiveScreen = async () => {
    try {
      const savedActiveScreen = await AsyncStorage.getItem('activeScreen');
      setActiveScreen(savedActiveScreen);
    } catch (error) {
      console.error('Error loading active screen:', error);
    }
  };

  const saveActiveScreen = async (screenName) => {
    try {
      setActiveScreen(screenName);
      await AsyncStorage.setItem('activeScreen', screenName);
    } catch (error) {
      console.error('Error saving active screen:', error);
    }
  };

  const login = async (userData) => {
    setIsLoggedIn(true);
    setUser(userData);
    try {
      await AsyncStorage.setItem('isLoggedIn', 'true');
      await AsyncStorage.setItem('user', JSON.stringify(userData));
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const logout = async () => {
    setIsLoggedIn(false);
    setUser(null);
    try {
      await AsyncStorage.removeItem('isLoggedIn');
      await AsyncStorage.removeItem('user');
      await AsyncStorage.removeItem('activeScreen');
      // Add the redirection logic here
      // navigation.navigate('Auth'); // Assuming you have a navigation object available
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  const authContextValue = {
    isLoggedIn,
    user,
    activeScreen,
    login,
    logout,
    saveActiveScreen,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
