import React, { useState, useRef } from 'react';
import { View, Text, TouchableHighlight, Modal, TouchableOpacity, ScrollView } from 'react-native-web';
import CreateSvg from '../assets/icons/createSvg';
import useStyles from './styles/mainAuthScreen.module';
import SearchInput from '../components/inputs/searchInput/searchInput';
import HeaderButton from '../components/buttons/headerButton';
import DataInput from '../components/inputs/textInput/textInput';
import SearchBody from '../components/searchBodies/searchBody';
import ShowAvatar from '../components/Avatar/ShowAvatar/showAvatar';
import BorderButton from '../components/buttons/borderButton';
import ChangeAvatar from '../components/Avatar/ChangeAvatar/changeAvatar';
import BackSvg from '../assets/icons/backSvg';

export default function ProfileScreen({ navigation }) {
    const styles = useStyles();
    const [inputText, setInputText] = useState({
      nickname: '',
    });
    const isFormValid = inputText.nickname;
  
    const emailContainerRef = useRef(null);
    const [emailFontSize, setEmailFontSize] = useState(48);
  
    const handleEmailLayout = () => {
      const emailContainerWidth = emailContainerRef.current.offsetWidth;
      const desiredMaxWidth = 300; // You can adjust this as needed
      const desiredMaxFontSize = 48; // You can adjust this as needed
      const emailFontSize = Math.min(
        desiredMaxFontSize,
        desiredMaxWidth / emailContainerWidth * desiredMaxFontSize
      );
      setEmailFontSize(emailFontSize);
    };
  
    useEffect(() => {
      handleEmailLayout(); // calculate initial font size
    }, []); // run only on mount
  
    return (
      <View style={styles.containerMain}>
        <View style={styles.profileSettingsContainer}>
          <View style={{ marginBottom: 10 }}>
            <ChangeAvatar />
          </View>
          <Text
            style={{
              color: '#000000',
              fontSize: 48,
              textAlign: 'center',
              marginBottom: 13,
            }}
          >
            Username
          </Text>
          <View
            style={{
              borderTopWidth: 1,
              borderBottomWidth: 1,
              borderColor: '#000000',
              paddingHorizontal: 10,
              paddingVertical: 15,
            }}
          >
            <View ref={emailContainerRef} onLayout={handleEmailLayout}>
              <Text
                style={{
                  color: '#000000',
                  fontSize: emailFontSize,
                  textAlign: 'center',
                  marginBottom: 13,
                }}
              >
                ilia.stavier@mail.ru
              </Text>
            </View>
          </View>
          <View>
            <HeaderButton
              title={'Изменить почту'}
              onPress={() => navigation.navigate('ChangeEmail')}
            />
          </View>
          <View>
            <HeaderButton
              title={'Изменить пароль'}
              onPress={() => navigation.navigate('ChangePassword')}
            />
          </View>
        </View>
        <View style={styles.topLeft}>
          <TouchableHighlight onPress={() => navigation.navigate('MainAuth')}>
            <BackSvg />
          </TouchableHighlight>
        </View>
      </View>
    );
  }
