import { View, TextInput } from "react-native-web";
import useStyles from "./textInput.module";

export default function DataInput({value, setValue, inputType, ...data}) {
  const styles = useStyles();

  const getMask = () => {
    if (inputType === 'password') {
      return {secureTextEntry: true};
    } else if (inputType === 'email') {
      return {keyboardType: 'email-address'};
    } else if (inputType === 'code') {
      return {keyboardType: 'numeric', maxLength: 4};
    } else {
      return {};
    }
  }

  return (
    <View style={styles.container}>
      <TextInput 
        {...data}
        placeholderTextColor={"#7C858E"}
        value={value}
        style={[styles.input, { paddingLeft: 21 }]}
        onChangeText={text => setValue(text)}
        {...getMask()}
      />
    </View>
  );
}
