import React, { useState } from "react";
import { View, Text, TextInput, TouchableHighlight, ScrollView } from "react-native-web";
import useStyles from "./searchInput.modules";
import SearchSvg from "../../../assets/icons/searchSvg";
import CrossSvg from "../../../assets/icons/crossSvg";
import SearchBody from "../../searchBodies/searchBody";

export default function SearchInput({ value, setValue, results, ...data }) {
  const [isFocused, setIsFocused] = useState(false);
  const [isClearVisible, setIsClearVisible] = useState(false);
  const styles = useStyles();

  const handleFocus = () => {
    setIsFocused(true);
    setIsClearVisible(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  const handleClear = () => {
    setValue("");
    setIsFocused(false);
    setIsClearVisible(false);
  };

  const handleSearchBodyPress = () => {
    setIsFocused(false);
    setIsClearVisible(false);
  };

  const handleInputChange = (text) => {
    setValue(text);
    setIsClearVisible(text.length > 0);
  };

  const filteredResults = value
    ? results.filter((result) => result.name.toLowerCase().includes(value.toLowerCase()))
    : results;

  return (
    <View style={styles.container}>
      <View style={styles.icon}>
        <SearchSvg />
      </View>
      <TextInput
        {...data}
        placeholderTextColor={"#7C858E"}
        value={value}
        style={[styles.input, { paddingLeft: 36 }]}
        onFocus={handleFocus}
        onBlur={handleBlur}
        onChangeText={handleInputChange}
      />
      {isClearVisible && (
        <TouchableHighlight onPress={handleClear}>
          <CrossSvg style={styles.dismissIcon} />
        </TouchableHighlight>
      )}
      {isFocused && (
        <ScrollView style={styles.dropdown}>
          {filteredResults.map((result, index) => (
            <SearchBody
              key={index}
              data={{
                avatarUrl: result.avatar || null,
                username: result.name,
                onPress: handleSearchBodyPress,
              }}
            />
          ))}
        </ScrollView>
      )}
    </View>
  );
}
