import { StyleSheet } from "react-native-web";

export default function useStyles() {
  return StyleSheet.create({
    container: {
      flexDirection: "row",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "#FFFFFF",
      borderRadius: 10,
    },
    input: {
      flex: 1,
      height: 44,
      fontSize: 48,
      paddingLeft: "10%", // Adjust the padding to your liking
      borderRadius: 10,
      backgroundColor: "#FFFFFF",
      color: "#000000",
      boxShadow: "2px 2px 2px rgba(0, 0, 0, 1)",
      width: "100%", // Adjust the width to your liking
    },
  });
}
