import React, { useState } from 'react';
import { View, Text, TouchableHighlight, Modal, TouchableOpacity } from 'react-native-web';
import CreateSvg from '../assets/icons/createSvg';
import useStyles from './styles/greetingsScreen.module';
import SearchInput from '../components/inputs/searchInput/searchInput';

export default function MainAuthScreen({ navigation }) {
  const styles = useStyles();
  const [showPopup, setShowPopup] = useState(false);

  return (
    <View style={styles.containerMain}>
      <View style={styles.messageContainer}>
        <Text style={styles.text}>Зарегистрируйтесь, чтобы начать общаться</Text>
      </View>
      <View style={styles.bottomLeft}>
        <TouchableHighlight onPress={() => setShowPopup(true)}>
          <CreateSvg />
        </TouchableHighlight>
      </View>
      <Modal visible={showPopup} transparent={true}>
        <View style={styles.popupContainer}>
          <TouchableOpacity onPress={() => setShowPopup(false)}>
            <Text style={styles.text}>Название канала</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}
