import React from 'react';
import { View, Text, StyleSheet } from 'react-native-web';
import ShowAvatar from '../Avatar/ShowAvatar/showAvatar';

const ForwardMessage = ({ imageUrl, nickname, message, own }) => {
  return (
    <View style={styles.container}>
      {own ? null : (
        <ShowAvatar imageUrl={imageUrl} profile={false} style={styles.avatar} />
      )}
      <View
        style={[
          styles.messageBox,
          own && styles.ownMessageBox,
        ]}
      >
        <Text style={[styles.nickname, own && styles.ownNickname]}>
          {nickname}
        </Text>
        <Text style={[styles.message, own && styles.ownMessage]}>{message}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'flex-end',
  },
  avatar: {
    alignSelf: 'flex-start',
  },
  messageBox: {
    marginLeft: 15,
    flex: 1,
    backgroundColor: '#E7DEDE',
    borderLeftWidth: 5,
    borderLeftColor: '#0076B9',
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 10,
    maxWidth: '40%',
    borderTopLeftRadius: 10,
    borderBottomLeftRadius: 10,
  },
  ownMessageBox: {
    backgroundColor: 'rgba(0, 118, 185, 0.35)',
    borderLeftColor: '#0076B9',
    borderLeftWidth: 5,
    marginLeft: 15,
    flex: 1,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 10,
    maxWidth: '40%',
    borderTopLeftRadius: 10,
    borderBottomLeftRadius: 10,
  },
  nickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  ownNickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  message: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
  },
  ownMessage: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
  },
});

export default ForwardMessage;
