import { StyleSheet, useWindowDimensions } from "react-native-web";

export default function useStyles() {
  const { width, height } = useWindowDimensions();
  return StyleSheet.create({
    container: {
      flexDirection: "row",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "#FFFFFF",
      zIndex: 1
    },
    icon: {
      position: "absolute",
      left: 10,
      zIndex: 1,
      paddingLeft: 30, 
      
    },
    dismissIcon: {
      position: "absolute",
      right: 10,
      top: "50%",
      transform: [{ translateY: -17 }]
    },
    dropdown: {
      position: "absolute",
      top: 50,
      left: 0,
      right: 0,
      backgroundColor: "#FFFFFF",
      borderRadius: 10,
      boxShadow: "2px 2px 2px rgba(0, 0, 0, 1)",
      zIndex: 1,
      padding: 10
    },
    input: {
      flex: 1,
      height: 44,
      width: 600,
      fontSize: 20, 
      margin: 10,                // Increase the font size            // Add paddingRight to move the text more to the right
      borderRadius: 10,
      backgroundColor: "#FFFFFF",
      color: "#000000",
      boxShadow: "2px 2px 2px rgba(0, 0, 0, 1)"
    },
  });
}
