import { StyleSheet, useWindowDimensions } from "react-native-web";

export default function useStyles() {
    const { width, height } = useWindowDimensions();
    return StyleSheet.create({
        container: { 
            flex: 1,
            justifyContent: 'center',
            flexDirection: 'row',
        },
        round: {
            alignItems: 'center',
            justifyContent: 'center',
            height: width * 0.1,
            width: width * 0.1,
            borderWidth: 0.7,
            borderRadius: width * 0.0645,
            borderColor: '#0000FF',
        },
        nonePhoto: {
            height: width * 0.0897, 
            width: width * 0.0897, 
            backgroundColor: '#D9D9D9', 
            borderRadius: width * 0.0645, 
            justifyContent: 'center', 
            alignItems: 'center',
        }
    });
}
