import React from 'react';
import { View, Text, StyleSheet } from 'react-native-web';
import ShowAvatar from '../Avatar/ShowAvatar/showAvatar';
import ForwardSvg from '../../assets/icons/forwardSvg';
import ForwardFocusSvg from '../../assets/icons/forwardFocusSvg';

const MessageBody = ({ imageUrl, nickname, role, message, own, channel }) => {
  const renderForwardButton = () => {
    return own ? null : (
      <View style={styles.forwardButton}>
        <ForwardSvg width={20} height={20} />
      </View>
    );
  };

  const renderRole = () => {
    return channel ? (
      <Text style={styles.role}>Role: {role}</Text>
    ) : null;
  };

  const renderAvatar = () => {
    return own ? null : (
      <View style={styles.avatarContainer}>
        <ShowAvatar imageUrl={imageUrl} profile={false} style={styles.avatar} />
      </View>
    );
  };

  return (
    <View style={own ? styles.ownContainer : styles.container}>
      {renderForwardButton()}
      {renderAvatar()}
      <View style={own ? styles.ownMessageBox : styles.messageBox}>
        <Text style={own ? styles.ownNickname : styles.nickname}>{nickname}</Text>
        {renderRole()}
        <Text style={own ? styles.ownMessage : styles.message}>{message}</Text>
        <Text style={styles.time}>{new Date().toLocaleTimeString([], { timeStyle: 'short' })}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'flex-start',
  },
  ownContainer: {
    flexDirection: 'row-reverse',
    alignItems: 'flex-start',
  },
  forwardButton: {
    position: 'absolute',
    top: 10,
    right: 10,
  },
  avatarContainer: {
    alignSelf: 'flex-start',
    marginTop: 5,
    marginRight: 15,
  },
  avatar: {
    width: 50,
    height: 50,
    borderRadius: 25,
  },
  messageBox: {
    flex: 1,
    backgroundColor: '#E7DEDE',
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
    borderBottomRightRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 10,
    maxWidth: '60%',
  },
  ownMessageBox: {
    flex: 1,
    backgroundColor: 'rgba(0, 118, 185, 0.35)',
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
    borderBottomLeftRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 10,
    maxWidth: '60%',
  },
  nickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  ownNickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  role: {
    fontSize: 16,
    color: '#0076B9',
    marginBottom: 5,
  },
  message: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
  },
  ownMessage: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
  },
  time: {
    fontSize: 12,
  },
});

export default ForwardMessage;
