import { StyleSheet, useWindowDimensions } from "react-native-web";

export default function useStyles(props) {
    const { width, height } = useWindowDimensions();
    const { profile } = props;
    
    const containerWidth = profile ? width * 0.9 : width * 0.4;
    const imageWidth = profile ? containerWidth : width * 0.4;
    const imageHeight = profile ? containerWidth : width * 0.4;
    const nonePhotoWidth = profile ? containerWidth : width * 0.9;
    const nonePhotoHeight = profile ? containerWidth : width * 0.9;
  
    return StyleSheet.create({
        container: {
            flexDirection: "row",
        },
        image: {
            width: imageWidth,
            height: imageHeight,
            borderRadius: 100,
        },
        round: {
            padding: width * 0.014,
            borderRadius: 100,
            marginRight: width * 0.025,
            borderWidth: width * 0.0025,
            borderColor: '#0076B9',
        },
        nonePhoto: {
            height: nonePhotoHeight,
            width: nonePhotoWidth,
            backgroundColor: "#D9D9D9",
            borderRadius: 100,
            justifyContent: "center",
            alignItems: "center",
        },
        info: {
            marginTop: width * 0.025,
            flexDirection: "column",
        },
        nickname: {
            fontSize: width * 0.05,
            fontWeight: "bold",
            marginBottom: width * 0.0125,
            marginTop: width * 0.011,
        },
        dateOfBirth: {
            fontSize: width * 0.035,
            color: "#999999",
        },
    });
}
