import { StyleSheet } from 'react-native-web';

export default function useStyles() {
  return StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FFFFFF',
      justifyContent: 'center',
      alignItems: 'center',
    },
    headerButton: {
      marginTop: 30, // задаем отступ сверху для кнопки
    },
    containerMain: {
      flex: 1,
      backgroundColor: 'rgba(0, 118, 185, 0.35)',
      justifyContent: 'center',
      alignItems: 'center',
    },
    bottomLeft: {
      position: 'absolute',
      bottom: 20,
      left: 20,
    },
    textContainer: {
      backgroundColor: '#FFFFFF',
      borderRadius: 10,
      padding: 20,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5,
      position: 'absolute',
      top: '50%',
      width: '50%',
      height: '50%',
      justifyContent: 'center',
      alignItems: 'center',
    },
    text:{
        color: '#000000',
        fontSize: 24,
        textAlign: 'center',
    }
  });
}
