import React, { createContext, useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [currentScreen, setCurrentScreen] = useState('');

  useEffect(() => {
    checkLoginStatus();
    loadPersistedState();
  }, []);

  const checkLoginStatus = async () => {
    try {
      const isLoggedInValue = await AsyncStorage.getItem('isLoggedIn');
      const userData = await AsyncStorage.getItem('user');
      if (isLoggedInValue === 'true' && userData) {
        setIsLoggedIn(true);
        setUser(JSON.parse(userData));
      }
    } catch (error) {
      console.error('Error during checkLoginStatus:', error);
    }
  };

  const loadPersistedState = async () => {
    try {
      const screen = await AsyncStorage.getItem('currentScreen');
      if (screen) {
        setCurrentScreen(screen);
      }
    } catch (error) {
      console.error('Error during loading persisted state:', error);
    }
  };

  const saveState = async (screen) => {
    try {
      await AsyncStorage.setItem('currentScreen', screen);
    } catch (error) {
      console.error('Error during saving state:', error);
    }
  };

  const login = async (userData) => {
    setIsLoggedIn(true);
    setUser(userData);
    try {
      await AsyncStorage.setItem('isLoggedIn', 'true');
      await AsyncStorage.setItem('user', JSON.stringify(userData));
      // localStorage.setItem('currentScreen', screen); // Remove this line
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const logout = async () => {
    setIsLoggedIn(false);
    setUser(null);
    try {
      await AsyncStorage.removeItem('isLoggedIn');
      await AsyncStorage.removeItem('user');
     // Use AsyncStorage to remove the item
      navigation.navigate(currentScreen); // Assuming you have a navigation object available
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  

  const authContextValue = {
    isLoggedIn,
    user,
    currentScreen,
    login,
    logout,
    setCurrentScreen: setCurrentScreenAndSaveState,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
