import { useEffect, useState } from 'react';
import React, { useContext } from 'react';
import { View, Image } from "react-native-web";
import useStyles from "./showAvatar.module";
import { Text } from 'react-native';
import PlusSvg from "../../../assets/icons/plusSvg"
import { ImageContext } from '../../../context/ImageContext';

export default function ShowAvatar({ imageSource, profile }) {
  const styles = useStyles({ profile });
  const { selectedImage } = useContext(ImageContext);

  let source = null;
  if (typeof imageSource === 'string') {
    source = { uri: imageSource };
  } else if (imageSource instanceof Uint8Array) {
    const byteCode = `data:image/jpeg;base64,${Buffer.from(imageSource).toString('base64')}`;
    source = { uri: byteCode };
  }

  return (
    <View style={styles.container}>
      <View style={styles.round}>
        {selectedImage ? (
          <Image style={styles.image} source={source} />
        ) : (
          <View style={styles.nonePhoto}>
            <PlusSvg style={{ transform: [{ scale: 0.5 }] }} />
          </View>
        )}
      </View>
    </View>
  );
}
