import React from 'react';
import { View, Text, StyleSheet } from 'react-native-web';
import ShowAvatar from '../Avatar/ShowAvatar/showAvatar';

const ForwardMessage = ({ imageUrl, nickname, message, own }) => {
  return (
    <View style={styles.container}>
      {own ? null : (
        <ShowAvatar imageUrl={imageUrl} profile={false} style={styles.avatar} />
      )}
      <View
        style={[
          styles.messageBox,
          own && { backgroundColor: '#0076B9', opacity: 0.35 },
        ]}
      >
        <Text style={styles.nickname}>{nickname}</Text>
        <Text style={styles.message}>{message}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'flex-end',
  },
  avatar: {
    alignSelf: 'flex-start',
  },
  messageBox: {
    marginLeft: 15,
    flex: 1,
    backgroundColor: '#E7DEDE',
    borderLeftWidth: 5,
    borderLeftColor: '#0076B9',
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 10,
    maxWidth: '40%',
  },
  nickname: {
    fontSize: 24,
    color: 'black',
    fontWeight: 'bold',
    marginBottom: 5,
  },
  message: {
    fontSize: 19,
    color: 'black',
    flexWrap: 'wrap',
  },
});

export default ForwardMessage;
