import React, { useState } from "react";
import {
  View,
  StyleSheet,
  Text,
  TouchableOpacity,
  Image,
  Modal,
} from "react-native-web";
import ChangeSvg from "../../assets/icons/changeSvg";
import DeleteSvg from "../../assets/icons/deleteSvg";
import AdminSvg from "../../assets/icons/adminSvg";
import AdminFocusSvg from "../../assets/icons/adminFocusSvg";
import HeaderButton from "../buttons/headerButton";
import DataInput from "../inputs/textInput/textInput";

export default function SettingsBody({ role, username, onPress, containerStyle }) {
  const [isHovered, setIsHovered] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleDelete = () => {
    // code to delete component goes here
  };

  const handleModal = () => {
    setIsModalVisible(!isModalVisible);
  };

  const [showPopup, setShowPopup] = useState(false);
  const [inputText, setInputText] = useState({
      nickname: '',
  });
  const isFormValid = inputText.nickname;

  return (
    <TouchableOpacity
      style={[
        styles.container,
        containerStyle,
        isHovered && { backgroundColor: "#E7DEDE" },
      ]}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <View style={styles.content}>
        <TouchableOpacity onPress={onPress}>
          <DeleteSvg />
        </TouchableOpacity>
        <Text style={styles.username}>{username}</Text>
        <Text style={styles.role}>{role}</Text>
        <TouchableOpacity onPress={handleModal}>
          <ChangeSvg />
        </TouchableOpacity>
      </View>

      <Modal visible={showPopup} transparent={true}>
                <View style={styles.popupContainer}>
                    <Text style={styles.text}>Название канала</Text>
                    <View style={{ marginBottom: 13, alignItems: 'center', }}>
                        <DataInput
                            value={inputText.nickname}
                            setValue={(text) => setInputText({ ...inputText, nickname: text })}
                            placeholder={""}
                            type={"nickname"}
                            flex={true}
                        />
                    </View>
                    <TouchableOpacity onPress={() => setShowPopup(false)}>
                        <View >
                            <HeaderButton title={"Создать"} onPress={() => console.log()} disabled={!isFormValid} />
                        </View>
                    </TouchableOpacity>
                </View>
            </Modal>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#FFFFFF",
    borderRadius: 26,
    paddingVertical: 12,
    paddingHorizontal: 16,
  },
  content: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  username: {
    fontSize: 24,
    color: "#000000",
    marginLeft: 20,
  },
  role: {
    fontSize: 24,
    color: "#0076B9",
    marginLeft: 20,
  },
  popupContainer: {
    backgroundColor: '#E7DEDE',
    borderRadius: 35,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: { width: 2, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 7,
    position: 'absolute',
    top: '50%', // set top to 50%
    left: '50%', // set left to 50%
    transform: [{ translateX: '-50%' }, { translateY: '-50%' }], // adjust position based on element size
    width: '20%',
    height: '20%',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column', // add this line to make items centered in a column
  }, 
});
